import React from "react";

const TaskList = ({ tasks, markDone }) => {
  // Get only the last 5 tasks
  const tasksToDisplay = tasks.slice(-5);

  return (
    <div style={styles.taskListContainer}>
      {/* Conditional rendering for no tasks */}
      {tasksToDisplay.length === 0 ? (
        <p style={styles.noTasksMessage}>No tasks added yet. Add a new task!</p>
      ) : (
        tasksToDisplay.map((task) => (
          <div key={task.id} style={styles.taskCard}>
            {/* The task content container to manage description overflow */}
            <div style={styles.taskContent}>
              {/* Removed h4 taskTitle as per previous instruction */}
              <p style={styles.taskDescription}>{task.description}</p>
            </div>
            <button style={styles.doneButton} onClick={() => markDone(task.id)}>
              Done
            </button>
          </div>
        ))
      )}
    </div>
  );
};

const styles = {
  taskListContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px', // Space between task cards
  },
  taskCard: {
    backgroundColor: '#e0e0e0', // Gray background for the cards
    borderRadius: '8px',
    padding: '15px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 2px 5px rgba(0,0,0,0.05)', // Subtle shadow
  },
  taskContent: {
    flexGrow: 1, // Allows content to take up available space
    marginRight: '15px',
    // Added styles to control text overflow within the content area
    overflow: 'hidden', // Hide overflowing content
    whiteSpace: 'normal', // Allow text to wrap
    display: '-webkit-box', // For multi-line ellipsis
    WebkitLineClamp: 2, // Limit to 2 lines (adjust as needed)
    WebkitBoxOrient: 'vertical',
    textOverflow: 'ellipsis', // Add ellipsis for overflow
    maxHeight: '40px', // Approximate max height for 2 lines of 14px text + line-height
  },
  // Removed taskTitle style as h4 is removed
  taskDescription: {
    fontSize: '14px',
    color: '#666',
    margin: 0,
    lineHeight: '1.4', // Adjust line height for better spacing between lines
  },
  doneButton: {
    padding: '8px 15px',
    backgroundColor: '#e0e0e0', // This is the background of the task card
    color: '#333', // Changed text color to a dark gray for contrast
    border: '1px solid #ccc', // Added a subtle border to make it distinct
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '14px',
    fontWeight: 'bold',
    minWidth: '70px',
    textAlign: 'center',
    transition: 'background-color 0.2s ease-in-out, color 0.2s ease-in-out, border-color 0.2s ease-in-out',
    boxShadow: '0 1px 2px rgba(0,0,0,0.1)',
  },
  noTasksMessage: {
    fontSize: '16px',
    color: '#888',
    textAlign: 'center',
    marginTop: '50px',
  },
};

export default TaskList;
