import React, { useState } from "react";

const TaskForm = ({ addTask }) => {
  const [title, setTitle] = useState("");
  const [description, setDesc] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim() && description.trim()) { // Trim to prevent adding empty tasks
      addTask({ title, description });
      setTitle("");
      setDesc("");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <label htmlFor="title" style={styles.label}>Title</label>
      <input
        id="title"
        style={styles.input}
        placeholder="Type a task title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />

      <label htmlFor="description" style={styles.label}>Description</label>
      <textarea
        id="description"
        style={styles.textarea}
        placeholder="Type a description"
        value={description}
        onChange={(e) => setDesc(e.target.value)}
        required
      />

      <button type="submit" style={styles.button}>Add</button>
    </form>
  );
};

const styles = {
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px", // Increased gap for better spacing
  },
  label: {
    fontSize: "16px", // Slightly larger font for labels
    color: '#555',
    marginBottom: '-5px', // Pull label closer to input
  },
  input: {
    padding: "12px", // More padding for larger input fields
    border: "1px solid #ccc", // Lighter border
    borderRadius: "5px", // Slightly more rounded corners
    fontSize: "16px",
    width: '100%', // Take full width of its container
    boxSizing: 'border-box', // Include padding and border in the element's total width and height
  },
  textarea: {
    padding: "12px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    minHeight: "100px", // Increased min-height for textarea
    resize: "vertical", // Allow vertical resizing
    fontSize: "16px",
    width: '100%',
    boxSizing: 'border-box',
  },
  button: {
    marginTop: "20px", // More margin above the button
    padding: "12px 25px", // Larger button padding
    backgroundColor: "#007bff", // Blue color as in the mockup
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: 'bold',
    alignSelf: 'flex-start', // Align button to the left
    transition: 'background-color 0.2s ease-in-out',
    boxShadow: '0 3px 5px rgba(0,123,255,0.2)', // Subtle shadow for the button
  },
  buttonHover: { // Example of hover if you want to implement it with a separate state or pseudo-class
    backgroundColor: '#0056b3',
  },
};

export default TaskForm;
