import React, { useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";

const App = () => {
  const [tasks, setTasks] = useState([]);

  const addTask = (newTask) => {
    setTasks([
      ...tasks,
      { id: tasks.length > 0 ? tasks[tasks.length - 1].id + 1 : 1, ...newTask },
    ]);
  };

  const markDone = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div style={styles.wrapper}>
      <h1 style={styles.header}>Task Management Sheet</h1>
      <div style={styles.pdfBox}>
        {/* Task Form (left) */}
        <div style={styles.column}>
          <h3 style={styles.subHeader}>Create New Task</h3>
          <TaskForm addTask={addTask} />
        </div>

        {/* Vertical Divider */}
        <div style={styles.verticalLine}></div>

        {/* Task List (right) */}
        <div style={styles.column}>
          <h3 style={styles.subHeader}>Recent Tasks</h3>
          <TaskList tasks={tasks} markDone={markDone} />
        </div>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    maxWidth: "1000px",
    margin: "40px auto",
    padding: "20px",
    fontFamily: "Times New Roman, serif",
    backgroundColor: "#ffffff",
  },
  header: {
    textAlign: "center",
    fontWeight: "bold",
    borderBottom: "2px solid #000000",
    paddingBottom: "10px",
    marginBottom: "20px",
  },
  pdfBox: {
    display: "flex",
    border: "1px solid #000000",
    borderRadius: "4px",
    boxShadow: "0 0 0 1px #000000",
    backgroundColor: "#ffffff",
  },
  column: {
    flex: 1,
    padding: "20px",
  },
  verticalLine: {
    width: "1px",
    backgroundColor: "#000000",
  },
  subHeader: {
    fontSize: "18px",
    marginBottom: "10px",
    textDecoration: "underline",
  },
};

export default App;
