import React, { useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";

const App = () => {
  const [tasks, setTasks] = useState([]);

  const addTask = (newTask) => {
    setTasks([
      ...tasks,
      { id: tasks.length > 0 ? tasks[tasks.length - 1].id + 1 : 1, ...newTask },
    ]);
  };

  const markDone = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>To-Do Task Manager</h1>

      {/* Combined Form Container */}
      <div style={styles.formWrapper}>
        {/* Left: TaskForm */}
        <div style={styles.leftPane}>
          <h2>New Task</h2>
          <TaskForm addTask={addTask} />
        </div>

        {/* Vertical Divider */}
        <div style={styles.divider}></div>

        {/* Right: TaskList */}
        <div style={styles.rightPane}>
          <h2>Task List</h2>
          <TaskList tasks={tasks} markDone={markDone} />
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "1000px",
    margin: "20px auto",
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
  },
  formWrapper: {
    display: "flex",
    backgroundColor: "#f9f9f9",
    border: "1px solid #ccc",
    borderRadius: "8px",
    overflow: "hidden",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  },
  leftPane: {
    flex: 1,
    padding: "20px",
  },
  rightPane: {
    flex: 1,
    padding: "20px",
  },
  divider: {
    width: "1px",
    backgroundColor: "#ccc",
  },
};

export default App;
