import React from "react";

const TaskList = ({ tasks, markDone }) => {
  const recentTasks = tasks.slice(-5); // show last 5

  return (
    <table style={styles.table}>
      <thead>
        <tr>
          <th style={styles.th}>Title</th>
          <th style={styles.th}>Description</th>
          <th style={styles.th}>Action</th>
        </tr>
      </thead>
      <tbody>
        {recentTasks.map((task) => (
          <tr key={task.id}>
            <td style={styles.td}>{task.title}</td>
            <td style={styles.td}>{task.description}</td>
            <td style={styles.td}>
              <button style={styles.button} onClick={() => markDone(task.id)}>
                Done
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

const styles = {
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  th: {
    border: "1px solid #000",
    padding: "8px",
    backgroundColor: "#eee",
    textAlign: "left",
  },
  td: {
    border: "1px solid #000",
    padding: "8px",
    verticalAlign: "top",
    fontSize: "14px",
  },
  button: {
    padding: "6px 10px",
    backgroundColor: "#000",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    fontSize: "12px",
  },
};

export default TaskList;
