import React, { useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";

const App = () => {
  // Initialize tasks as an empty array
  const [tasks, setTasks] = useState([]);

  const addTask = (newTask) => {
    setTasks([
      ...tasks,
      { id: tasks.length > 0 ? tasks[tasks.length - 1].id + 1 : 1, ...newTask },
    ]);
  };

  const markDone = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div style={styles.appContainer}>
      <div style={styles.laptopFrame}>
        <div style={styles.screen}>
          <div style={styles.contentWrapper}>
            {/* Task Form (left) */}
            <div style={styles.column}>
              <h3 style={styles.subHeader}>Add a Task</h3>
              <TaskForm addTask={addTask} />
            </div>

            {/* Task List (right) */}
            <div style={styles.column}>
              <TaskList tasks={tasks} markDone={markDone} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  appContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundColor: '#f0f2f5', // Light background for the overall page
    fontFamily: 'Arial, sans-serif',
  },
  laptopFrame: {
    width: '900px', // Approximate width based on the image
    height: '600px', // Approximate height
    backgroundColor: '#333', // Dark frame color
    borderRadius: '20px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.3)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '20px',
    position: 'relative',
    overflow: 'hidden',
  },
  screen: {
    width: 'calc(100% - 40px)', // Inner screen width
    height: 'calc(100% - 40px)', // Inner screen height
    backgroundColor: '#f9f9f9', // Screen background color
    borderRadius: '8px',
    boxShadow: 'inset 0 0 10px rgba(0,0,0,0.1)',
    overflow: 'auto', // In case content overflows
  },
  contentWrapper: {
    display: 'flex',
    padding: '30px',
    gap: '40px', // Space between columns
    height: '100%',
    boxSizing: 'border-box', // Include padding in height calculation
  },
  column: {
    flex: 1,
    padding: '10px 0',
  },
  subHeader: {
    fontSize: '24px',
    fontWeight: 'normal', // The mockup's header is not bold
    marginBottom: '30px', // More space below the header
    color: '#333',
  },
};

export default App;
