import React from "react";

const TaskList = ({ tasks, markDone }) => {
  const visibleTasks = tasks.slice(-5);

  return (
    <ul style={styles.list}>
      {visibleTasks.map((task) => (
        <li key={task.id} style={styles.item}>
          <div style={styles.textContainer}>
            <strong style={styles.title}>{task.title}</strong>
            <p style={styles.description}>{task.description}</p>
          </div>
          <button onClick={() => markDone(task.id)} style={styles.doneButton}>
            Done
          </button>
        </li>
      ))}
    </ul>
  );
};

const styles = {
  list: {
    listStyle: "none",
    padding: 0,
    margin: 0,
  },
  item: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    backgroundColor: "#d9d9d9",
    padding: "15px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #d9d9d9",
    flexWrap: "wrap",
  },
  textContainer: {
    flexGrow: 1,
    marginRight: "10px",
    wordWrap: "break-word",
    maxWidth: "calc(100% - 80px)",
  },
  title: {
    fontSize: "14px",
    fontWeight: "bold",
  },
  description: {
    margin: "5px 0 0 0",
    fontSize: "12px",
    color: "#333",
    whiteSpace: "pre-wrap",
    wordBreak: "break-word",
  },
  doneButton: {
    backgroundColor: "#d9d9d9",
    color: "#000000",
    border: "1.5px solid #b3b3b3",
    padding: "6px 12px",
    cursor: "pointer",
    borderRadius: "4px",
    fontSize: "12px",
    fontWeight: "bold",
    height: "fit-content",
  },
};

export default TaskList;
