import React from "react";

const TaskList = ({ tasks, markDone }) => {
  return (
    <div style={styles.taskListContainer}>
      {tasks.map((task) => (
        <div key={task.id} style={styles.taskCard}>
          <div style={styles.taskContent}>
            <h4 style={styles.taskTitle}>{task.title}</h4>
            <p style={styles.taskDescription}>{task.description}</p>
          </div>
          <button style={styles.doneButton} onClick={() => markDone(task.id)}>
            Done
          </button>
        </div>
      ))}
    </div>
  );
};

const styles = {
  taskListContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px', // Space between task cards
  },
  taskCard: {
    backgroundColor: '#e0e0e0', // Gray background for the cards
    borderRadius: '8px',
    padding: '15px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 2px 5px rgba(0,0,0,0.05)', // Subtle shadow
  },
  taskContent: {
    flexGrow: 1, // Allows content to take up available space
    marginRight: '15px',
  },
  taskTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
    margin: '0 0 5px 0',
    color: '#333',
  },
  taskDescription: {
    fontSize: '14px',
    color: '#666',
    margin: 0,
  },
  doneButton: {
    padding: '8px 15px',
    backgroundColor: '#6c757d', // A subtle gray/blue for the "Done" button
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '14px',
    fontWeight: 'bold',
    minWidth: '70px', // Ensure button has consistent width
    textAlign: 'center',
    transition: 'background-color 0.2s ease-in-out',
    boxShadow: '0 2px 3px rgba(0,0,0,0.1)',
  },
  doneButtonHover: { // Example of hover if you want to implement it with a separate state or pseudo-class
    backgroundColor: '#5a6268',
  },
};

export default TaskList;
