import React, { useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";

const App = () => {
  const [tasks, setTasks] = useState([]);

  const addTask = (newTask) => {
    setTasks([
      ...tasks,
      { id: tasks.length > 0 ? tasks[tasks.length - 1].id + 1 : 1, ...newTask },
    ]);
  };

  const markDone = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div style={styles.appContainer}>
      <div style={styles.mainContentBox}>
        {/* Task Form (left) */}
        <div style={styles.column}>
          <h3 style={styles.subHeader}>Add a Task</h3>
          <TaskForm addTask={addTask} />
        </div>

        {/* Vertical Divider Line */}
        <div style={styles.verticalLine}></div>

        {/* Task List (right) */}
        <div style={styles.column}>
          {/* Removed: <h3 style={styles.subHeader}>Recent Tasks</h3> */}
          <TaskList tasks={tasks} markDone={markDone} />
        </div>
      </div>
    </div>
  );
};

const styles = {
  appContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
    minHeight: '100vh',
    padding: '40px 20px',
    backgroundColor: '#f0f2f5',
    fontFamily: 'Arial, sans-serif',
    boxSizing: 'border-box',
  },
  mainContentBox: {
    display: 'flex',
    width: '900px',
    backgroundColor: '#ffffff',
    borderRadius: '8px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)',
    padding: '30px',
    gap: '30px',
    boxSizing: 'border-sizing', // Fixed typo from 'border-box'
  },
  column: {
    flex: 1,
  },
  verticalLine: {
    width: '1px',
    backgroundColor: '#ddd',
    margin: '0 15px',
    alignSelf: 'stretch',
  },
  subHeader: {
    fontSize: '24px',
    fontWeight: 'normal',
    marginBottom: '30px',
    color: '#333',
  },
};

export default App;
