import React, { useState } from "react";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";

const App = () => {
  const [tasks, setTasks] = useState([]);

  const addTask = (newTask) => {
    setTasks([
      ...tasks,
      { id: tasks.length > 0 ? tasks[tasks.length - 1].id + 1 : 1, ...newTask },
    ]);
  };

  const markDone = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div style={styles.appContainer}>
      {/* Reverted to a simpler container, like the initial PDF box concept */}
      <div style={styles.mainContentBox}>
        {/* Task Form (left) */}
        <div style={styles.column}>
          <h3 style={styles.subHeader}>Add a Task</h3>
          <TaskForm addTask={addTask} />
        </div>

        {/* Vertical Divider Line */}
        <div style={styles.verticalLine}></div>

        {/* Task List (right) */}
        <div style={styles.column}>
          <h3 style={styles.subHeader}>Recent Tasks</h3> {/* Added a header for consistency, matching original */}
          <TaskList tasks={tasks} markDone={markDone} />
        </div>
      </div>
    </div>
  );
};

const styles = {
  appContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start', // Align items to the start, not center for vertical space
    minHeight: '100vh',
    padding: '40px 20px', // Add some padding around the main box
    backgroundColor: '#f0f2f5', // Light background for the overall page
    fontFamily: 'Arial, sans-serif',
    boxSizing: 'border-box', // Ensure padding is included in element's total width/height
  },
  mainContentBox: {
    display: 'flex',
    width: '900px', // Set a fixed width for the content area
    backgroundColor: '#ffffff', // White background for the main box
    borderRadius: '8px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)', // Subtle shadow for the box
    padding: '30px', // Padding inside the main box
    gap: '30px', // Gap between columns, adjusted for the line
    boxSizing: 'border-box',
  },
  column: {
    flex: 1, // Columns take equal space
    // No specific padding here, handled by mainContentBox padding and gap
  },
  verticalLine: {
    width: '1px', // The straight line itself
    backgroundColor: '#ddd', // A light gray for the divider
    margin: '0 15px', // Adjust margin to give space around the line
    alignSelf: 'stretch', // Make the line stretch the full height of the container
  },
  subHeader: {
    fontSize: '24px',
    fontWeight: 'normal',
    marginBottom: '30px',
    color: '#333',
  },
};

export default App;
